import React, { Component } from 'react'
import './App.css';

class App extends Component {

    constructor(props) {
        super(props)
        this.state = {
            message: null,
            firstN: "",
            lastN: ""
        };
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

//FUNCTIONS
    //get response from the back end
    componentDidMount() {
        // Make a GET-request to our backend
        fetch('/api/test')
        // Map the result to a JSON-object
            .then(response => response.json())
            // Put the message in the state
            .then(response => {
                this.setState({ message: response.message })
            })
    }


    handleChange(e) {
        this.setState({firstN: e.target.firstN})
        //this.setState({lastN: e.target.lastN})

    }

    handleSubmit(e) {
        e.preventDefault()
        // this.setState({firstN: e.target.firstN})
        // this.setState({lastN: e.target.lastN})

        alert("This name was submitted" + this.state.firstN + ' ' + this.state.lastN)
        //let firstN = this.setState({firstN: event.target.firstN})

        console.log("User Full Name:" + this.state.firstN)
    }




//RENDERING
    render() {
        return (
            <div className="App">
                <p>Message from backend: {this.state.message}</p>
                <form>
                    Enter First Name:<br/>
                    <input id ="FirstName" type="text" firstN={this.state.value} onChange={this.handleChange} /><br/>
                    {/*Enter Last Name:<br/>*/}
                    {/*<input id="LastName" type="text" /><br/>*/}
                    <button onClick={this.handleSubmit} type="submit">Submit</button>
                </form>
            </div>
        )
    }
}


export default App;
