import React, { Component } from 'react'
import './App.css';

class App extends Component {

    constructor() {

    }
    state = {
        message: null,
        firstN: "",
        lastN: ""
    }


    render() {
        return (
            <div className="App">
                <p>Message from backend: {this.state.message}</p>
                <form>
                    Enter First Name:<br/>
                    <input id ="FirstName" type="text" name="firstname"/><br/>
                    Enter Last Name:<br/>
                    <input id="LastName" type="text" name="lastname"/><br/>
                    <button onClick={this.insertUser} type="button">Submit</button>
                </form>
            </div>
        )
    }


//FUNCTIONS
    //get response from the back end
    componentDidMount() {
        // Make a GET-request to our backend
        fetch('/api/test')
        // Map the result to a JSON-object
            .then(response => response.json())
            // Put the message in the state
            .then(response => {
                this.setState({ message: response.message })
            })
    }


    insertUser(){
        let first = this.state.firstN
        let last = this.state.lastN

        console.log("User Full Name: " + first + last)
    }
}

export default App;
